// import Link from "next/link";
import React, { Component } from "react";
import css from "./Navbar.css";
import NavLink from "./NavLink";

class Navbar extends Component {
  constructor(props) {
    super(props);
    this.myRef = React.createRef()

  }

  state = {
    showMobileDropDown: false,
    showBorderBottom: false,
  };

  toggleMobileNav = () => {
    console.log("Mobile Nav");

    this.setState({
      showMobileDropDown: !this.state.showMobileDropDown,
    //   showBorderBottom: !this.state.showBorderBottom
    });
  };

  addBorderBottom = () => {
    // const node = this.myRef.current;

    // if (node) {
    //   if (node.offsetHeight == 3) {
    //     node.style.borderBottom = "0px";
    //   } else {
    //     node.style.borderBottom = "3px solid yellow";
    //   }
    // }
    console.log("Blah")
    console.log(this.state.showMobileDropDown)
    this.setState({
        showBorderBottom: !this.state.showBorderBottom
      });
  };

  render() {

    return (
      // const Navbar = props => (
      <div className={css.OuterNavContainer}>
        <div
          onClick={this.toggleMobileNav}
          className={`${css.navbar} ${css.regNav}`}
        >
          <NavLink name="About Us" url="page/about/" style={css.navLink} />
          <NavLink name="Portfolio" url="case-studies/" style={css.navLink} />
          <NavLink name="What We Do" url="#what-we-do" style={css.navLink} />
          <NavLink
            name="Who We Serve"
            url="#who-we-serve"
            style={css.navLink}
          />
          <NavLink name="Contact Us" url="page/contact/" style={css.navLink} />
          <img
            className={css.hamburgerMenu}
            src="https://labs.chiedo.com/wp-content/themes/chiedolabs/img/mob-trig.png"
            alt="Mobile menu hamburger icon"
            title="Mobile menu icon"
          />
        </div>
        <div
          ref={this.myRef}
          onTransitionEnd={this.addBorderBottom}
          className={`${css.mobileNav} 
          ${this.state.showMobileDropDown ? css.showMobileDropDown : css.hideMobileDropDown}
          ${this.state.showBorderBottom || this.state.showMobileDropDown   ? css.showBorderBottom : css.hideBorderBottom}
          `}
        >
          <NavLink
            name="About Us"
            url="page/about/"
            style={css.mobileNavLink}
          />
          <NavLink
            name="Portfolio"
            url="case-studies/"
            style={css.mobileNavLink}
          />
          <NavLink
            name="What We Do"
            url="#what-we-do"
            style={css.mobileNavLink}
          />
          <NavLink
            name="Who We Serve"
            url="#who-we-serve"
            style={css.mobileNavLink}
          />
          <NavLink
            name="Contact Us"
            url="page/contact/"
            style={css.mobileNavLink}
          />
        </div>
      </div>
    );
  }
}

export default Navbar;
