import Header from "../components/Header/Header";
import Navbar from "../components/Navbar/Navbar";
import MainSection from "../components/MainSection/MainSection";
import Button from "../components/Button/Button";
import HireOurTeam from "../components/HireOurTeam/HireOurTeam";
import HireOurTeamCss from "../components/HireOurTeam/HireOurTeam.css";
import TwoColumnEvenWithPicCss from "../components/TwoColumnEvenWithPic/TwoColumnEvenWithPic.css";
import TwoColumnEvenWithPic from "../components/TwoColumnEvenWithPic/TwoColumnEvenWithPic";

import FoundingWebDevs from "../components/FoundingWebDevs/FoundingWebDevs";
import FoundingWebDevsCss from "../components/FoundingWebDevs/FoundingWebDevs.css";
import Head from "next/head";
import App from "../components/App/App.js";

const Index = () => (
  <div>
    <Head>
      <title>
        Chiedo Labs: Custom Web Development - Node.js | React | Rails | PHP
      </title>
      <meta
        name="viewport"
        content="minimum-scale=1, initial-scale=1, width=device-width, shrink-to-fit=no"
      />
    </Head>
    <style jsx global>{`
      body {
        font-family: "Open Sans", sans-serif;
        color: #2c343b;
        background-color: #f2f2f2;
        boxsizing: "border-box";
      }
    `}</style>
    <Header imageUrl="https://labs.chiedo.com/wp-content/themes/chiedolabs/img/b-logo-white.png?ver=1.11" />

    <Navbar />
    <MainSection
      tagline={
        <div>
          Your New
          <br />
          Web Developers
        </div>
      }
      description="We’re a team of web developers passionate about building stable, maintainable software for tech startups and enterprise customers."
    />
    <HireOurTeam
      classNames={`${HireOurTeamCss.FullWidth} ${HireOurTeamCss.hireOurTeam}`}
    />
    <TwoColumnEvenWithPic
      titleFirstWord="WHAT"
      restOfTitle="WE D0"
      flexClass={TwoColumnEvenWithPicCss.flexNormal}
      restOfText={
        <React.Fragment>
          <p className={TwoColumnEvenWithPicCss.paragraph}>We build and maintain:</p>
          <p className={TwoColumnEvenWithPicCss.paragraph}>
            Web Front-ends with HTML, CSS, and a library called React.
          </p>
          <p className={TwoColumnEvenWithPicCss.paragraph}>
            Web Back-ends using NodeJS, Ruby on Rails, PHP, MySQL, AWS, and
            Heroku.
          </p>
          <p className={TwoColumnEvenWithPicCss.paragraph}>
            Custom Websites from the ground up as opposed to using pre-existing
            templates.
          </p>
        </React.Fragment>
      }
    />
    <FoundingWebDevs />
    <TwoColumnEvenWithPic
      titleFirstWord="WHO"
      restOfTitle="WE SERVE"
      flexClass={TwoColumnEvenWithPicCss.flexReverse}
      restOfText={
        <React.Fragment>
          <p className={TwoColumnEvenWithPicCss.paragraph}>
            <span className={TwoColumnEvenWithPicCss.highlightedWords}>Enterprise customers</span> hire us to build and maintain the web apps
            critical to their operations.
          </p>
          <p className={TwoColumnEvenWithPicCss.paragraph}>
          <span className={TwoColumnEvenWithPicCss.highlightedWords}>Tech startups</span> partner with us to build and maintain the web
            front-ends and back-ends at the core of their business.
          </p>
        </React.Fragment>
      }
    />
  </div>
);

export default Index;
